import React, { useState } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";

import { Increment } from "./Actions/Increment";

import { useSelector, useDispatch } from "react-redux";
const CountDown = () => {
  const myState = useSelector((state) => state.reducer);

  const dispatch = useDispatch();
  const [timer, setTimer] = useState({
    h: 0,

    m: 0,
    s: 0,
  });
  const secondsToTime = (secs) => {
    let hours = Math.floor(secs / (60 * 60));

    let divisor_for_minutes = secs % (60 * 60);
    let minutes = Math.floor(divisor_for_minutes / 60);

    let divisor_for_seconds = divisor_for_minutes % 60;
    let seconds = Math.ceil(divisor_for_seconds);

    setTimer({
      h: hours,

      m: minutes,
      s: seconds,
    });
  };

  const incTimer = () => {
    if (timer.s >= 0) Increment(secondsToTime);
  };

  const Increment = (fn) => dispatch({ type: "INCREMENT", secToTime: fn });

  return (
    <Container fluid style={{ padding: "0px 25px" }}>
      <Row
        style={{
          justifyContent: "space-between",
          padding: "15px 0px ",
          alignItems: "center",
        }}
      >
        <Col xs={4}>
          <h5>CountDown App</h5>
        </Col>
      </Row>

      <Container>
        <div className="container mt-4  justify-content-center">
          <div className="mx-auto justify-content-center  text-center">
            <span>{myState}</span>
          </div>
          <div className="mt-3 text-center ">
            <Button
              className="m-2"
              variant="info"
              onclick={() => {
                incTimer();
              }}
            >
              +
            </Button>

            <Button className="m-2" variant="primary">
              Start
            </Button>
            <Button className="m-2" variant="danger">
              Stop
            </Button>
            <Button className="m-2" variant="success">
              Lap
            </Button>
            <Button className="m-2" variant="warning">
              Reset
            </Button>
            <Button className="m-2" variant="info">
              -
            </Button>
          </div>
        </div>
        <div className="container"></div>
      </Container>
    </Container>
  );
};

export default CountDown;
