import { SET_PRODUCTS } from "../types/Types";
import axios from "axios";
import { WEATHER_API_KEY } from "../../GlobalConstants/ApiKeys";
export const Increment = () => {
  return {
    type: "INCREMENT",
  };
};

export const Decrement = () => {
  return {
    type: "DECREMENT;",
  };
};

export const Start = () => {
  return {
    type: "START",
  };
};
export const Stop = () => {
  return {
    type: "STOP",
  };
};
//Declaration of City Name
let city = "Latur";
//Declaration of Country Name
let country = "India";
//Declaration of Unit Name
let units = "metric";
//Declaration of APi URL
let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${WEATHER_API_KEY}&units=${units}`;

export const fetchProducts = () => {
  return async function (dispatch) {
    // const res = async function getUser() {
    //   try {
    //     const response = await axios.get(apiUrl);
    //     console.log(response);
    //   } catch (error) {
    //     console.error(error);
    //   }
    // };
    const getUserAccount = () => {
      return axios.get(apiUrl);
    };

    const data = getUserAccount();

    dispatch(setProducts(data.products));
  };
};

export const setProducts = (products = null) => {
  if (products) {
    return {
      type: SET_PRODUCTS,
      payload: products,
    };
  }

  return {
    type: SET_PRODUCTS,
    payload: [],
  };
};
