import { SET_PRODUCTS } from "../types/Types";
import axios from "axios";
import { WEATHER_API_KEY } from "../../GlobalConstants/ApiKeys";
export const Increment = () => {
  return {
    type: "INCREMENT",
  };
};

export const Decrement = () => {
  return {
    type: "DECREMENT;",
  };
};

export const Start = () => {
  return {
    type: "START",
  };
};
export const Stop = () => {
  return {
    type: "STOP",
  };
};
//Declaration of City Name
let city = "Latur";
//Declaration of Country Name
let country = "India";
//Declaration of Unit Name
let units = "metric";
//Declaration of APi URL
let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${WEATHER_API_KEY}&units=${units}`;

export const fetchProducts = () => {
  return async function (dispatch) {
    const res = async () => {
      try {
        const response = await axios.get(apiUrl);
        console.log(response);
        return response;
      } catch (error) {
        console.error(error);
      }

      axios
        .get(apiUrl)
        .then(function (response) {
          if (mounted) {
            setWeatherDetails(response);
            // console.log(response);
            setLoader(false);
          }
        })
        .catch(function (error) {
          // handle error
          //   console.log(error);
          setErrorMsg("Data Not Available");
          setLoader(false);
        });
    };
    console.log(res);
    const data = res;

    dispatch(setProducts(data.data));
  };
};

export const setProducts = (products = null) => {
  console.log(products);
  if (products) {
    return {
      type: SET_PRODUCTS,
      payload: products,
    };
  }

  return {
    type: SET_PRODUCTS,
    payload: [],
  };
};
useEffect(() => {
  let mounted = true;
  //Function For Getting Api Data And Setting in WeatherDetails State
  const fetchWeather = () => {
    axios
      .get(apiUrl)
      .then(function (response) {
        if (mounted) {
          setWeatherDetails(response);
          // console.log(response);
          setLoader(false);
        }
      })
      .catch(function (error) {
        // handle error
        //   console.log(error);
        setErrorMsg("Data Not Available");
        setLoader(false);
      });
  };
  fetchWeather();
  return function cleanup() {
    mounted = false;
  };
}, []);
