import React, { useState } from "react";
import { Form, Button, Container, Alert } from "react-bootstrap";
import { Formik } from "formik";
import * as Yup from "yup";
import { Fragment } from "react";

const pinCodeRegExp = /^[1-9][0-9]*$/;

const passwordRegExp =
  /^(?=.*\d)(?=.*[A-Z])(?=.*[a-z])(?=.*[a-zA-Z!#$@^%&? "])[a-zA-Z0-9!#$@^%&?]{8,20}$/;
const validationSchema = Yup.object().shape({
  firstName: Yup.string()
    .min(2, "*First Name must have at least 2 characters")
    .max(100, "*First Name can't be longer than 100 characters")
    .required("*First Name is required"),
  lastName: Yup.string()
    .min(2, "*Last Name must have at least 2 characters")
    .max(100, "*Last Name can't be longer than 100 characters")
    .required("*Last Name is required"),
  email: Yup.string()
    .email("*Must be a valid email address")
    .max(100, "*Email must be less than 100 characters")
    .required("*Email is required"),
  city: Yup.string()
    .min(2, "*City Name must have at least 2 characters")
    .max(100, "*City Name can't be longer than 100 characters")
    .required("*City Name is required"),
  state: Yup.string()
    .min(2, "*State Name must have at least 2 characters")
    .max(100, "*State Name can't be longer than 100 characters")
    .required("*State Name is required"),
  zip: Yup.string("Must be number")
    .min(6, "*Pincode must be 6 digit")
    .max(6, "*Pincode must be 6 digit")
    .required("*Zip Code  is required")
    .matches(pinCodeRegExp, " Must be Positive Number"),
  password: Yup.string()
    .required("*Password  is required")
    .matches(
      passwordRegExp,
      "Password min be 8 Character,Password max be 20 Character,At least one uppercase character ,At least one lowercase character ,At least one digit ,At least one special character  "
    ),
});

const FormValidation = () => {
  const [show, setShow] = useState(true);

  return (
    <Fragment>
      <Container
        fluid
        style={{
          padding: "30px",
          paddingTop: "0px",
          paddingBottom: "15px",
        }}
      >
        <Formik
          initialValues={{
            firstName: "",
            lastName: "",
            email: "",
            city: "",
            state: "",
            zip: "",
            password: "",
          }}
          validationSchema={validationSchema}
          onSubmit={(values, { setSubmitting, resetForm }) => {
            // When button submits form and form is in the process of submitting, submit button is disabled
            setSubmitting(true);

            // Simulate submitting to database, shows us values submitted, resets form
            setTimeout(() => {
              // alert(" Data is submitted successfully");

              console.log(values);
              setShow(true);
              resetForm();
              setSubmitting(false);
            }, 1000);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
          }) => (
            <Form onSubmit={handleSubmit} className="mt-3">
              {/*First Name input Field*/}
              <Form.Group controlId="firstName" className="mb-3">
                <Form.Label> First name :</Form.Label>
                <Form.Control
                  type="text"
                  name="firstName"
                  placeholder="First Name"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.firstName}
                  isInvalid={touched.firstName && errors.firstName}
                  isValid={touched.firstName && !errors.firstName}
                />

                <Form.Control.Feedback type="invalid">
                  {errors.firstName}
                </Form.Control.Feedback>
              </Form.Group>
              {/*Last Name input Field*/}
              <Form.Group controlId="lastName" className="mb-3">
                <Form.Label> Last name :</Form.Label>
                <Form.Control
                  type="text"
                  name="lastName"
                  placeholder="Last Name"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.lastName}
                  isInvalid={touched.lastName && errors.lastName}
                  isValid={touched.lastName && !errors.lastName}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.lastName}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Password input Field*/}
              <Form.Group controlId="password" className="mb-3">
                <Form.Label> Password :</Form.Label>
                <Form.Control
                  type="text"
                  name="password"
                  placeholder="Password"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.password}
                  isInvalid={touched.password && errors.password}
                  isValid={touched.password && !errors.password}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.password}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Email input Field*/}
              <Form.Group controlId="email" className="mb-3">
                <Form.Label> Email :</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Email"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.email}
                  isInvalid={touched.email && errors.email}
                  isValid={touched.email && !errors.email}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.email}
                </Form.Control.Feedback>
              </Form.Group>

              {/*City input Field*/}
              <Form.Group controlId="city" className="mb-3">
                <Form.Label> City :</Form.Label>
                <Form.Control
                  type="text"
                  name="city"
                  placeholder="City"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.city}
                  isInvalid={touched.city && errors.city}
                  isValid={touched.city && !errors.city}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.city}
                </Form.Control.Feedback>
              </Form.Group>

              {/*State input Field*/}
              <Form.Group controlId="state" className="mb-3">
                <Form.Label> State :</Form.Label>
                <Form.Control
                  type="text"
                  name="state"
                  placeholder="State"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.state}
                  isInvalid={touched.state && errors.state}
                  isValid={touched.state && !errors.state}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.state}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Zip input Field*/}
              <Form.Group controlId="zip" className="mb-3">
                <Form.Label> Zip :</Form.Label>
                <Form.Control
                  type="text"
                  name="zip"
                  placeholder="Zip Code"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.zip}
                  isInvalid={touched.zip && errors.zip}
                  isValid={touched.zip && !errors.zip}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.zip}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Submit button that is disabled after button is clicked/form is in the process of submitting*/}
              <Button
                variant="primary"
                type="submit"
                className="mt-5"
                disabled={isSubmitting}
              >
                Submit
              </Button>
            </Form>
          )}
        </Formik>

        <div>
          {show === "true" ? (
            <Alert variant="danger" onClose={() => setShow(false)} dismissible>
              <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
              <p>
                Change this and that and try again. Duis mollis, est non commodo
                luctus, nisi erat porttitor ligula, eget lacinia odio sem nec
                elit. Cras mattis consectetur purus sit amet fermentum.
              </p>
            </Alert>
          ) : (
            ""
          )}
        </div>
      </Container>
    </Fragment>
  );
};

export default FormValidation;
