import { SET_PRODUCTS } from "../types/Types";
import axios from "axios";
export const Increment = () => {
  return {
    type: "INCREMENT",
  };
};

export const Decrement = () => {
  return {
    type: "DECREMENT;",
  };
};

export const Start = () => {
  return {
    type: "START",
  };
};
export const Stop = () => {
  return {
    type: "STOP",
  };
};

export const fetchProducts = () => {
  return async function (dispatch) {
    const fetchWeather = () => {
      axios
        .get(apiUrl)
        .then(function (response) {
          if (mounted) {
            setWeatherDetails(response);
            // console.log(response);
            setLoader(false);
          }
        })
        .catch(function (error) {
          // handle error
          //   console.log(error);
          setErrorMsg("Data Not Available");
          setLoader(false);
        });
    };
    fetchWeather();
    const data = await res.json();

    dispatch(setProducts(data.products));
  };
};

export const setProducts = (products = null) => {
  if (products) {
    return {
      type: SET_PRODUCTS,
      payload: products,
    };
  }

  return {
    type: SET_PRODUCTS,
    payload: [],
  };
};
