  //         </>
  //       ) : (
  //         <>
  //           <div
  //             style={{
  //               padding: "200px",
  //               color: "red",
  //               textAlign: "center",
  //             }}
  //           >
  //             <CircularProgress color="secondary" />
  //             <h1>Loading Data Please wait...</h1>
  //           </div>
  //         </>
  //       )}
  //     </>
  //   );
  // };

  const renderRowSubComponent = useCallback(
    ({ row }) =>
      !loading ? (
        <ReactGlobalTable
          columns={subTableColumns}
          data={subTableData}
          className={"rm-to-table"}
          defaultPageSize={5}
          showPagination={false}
          showGlobalFilter={false}
        />
      ) : (
        <LoadinScreen />
      ),
    [subTableColumns, subTableData, loading]
  );

  return (
    <div className="app">
      <div className="text-center"> {errorMsg}</div>
      {loader ? (
        <>
          <div
            style={{
              padding: "200px",
              color: "red",
              textAlign: "center",
            }}
          >
            <CircularProgress color="secondary" />
            <h1>Loading Data Please wait...</h1>
          </div>
        </>
      ) : (
        <>
          {users && (
            <CommonReactTable
              data={users}
              columns={columns}
              tableTitle={"Post Management Table"}
              showPagination={false}
              renderRowSubComponent={renderRowSubComponent}
            />
          )}
        </>
      )}
    </div>
  );
};

