import { useState } from "react";
import { SET_PRODUCTS } from "../types/Types";
import axios from "axios";
import { WEATHER_API_KEY } from "../../GlobalConstants/ApiKeys";
export const Increment = () => {
  return {
    type: "INCREMENT",
  };
};

export const Decrement = () => {
  return {
    type: "DECREMENT;",
  };
};

export const Start = () => {
  return {
    type: "START",
  };
};
export const Stop = () => {
  return {
    type: "STOP",
  };
};

//Declaration of City Name
let city = "Latur";
//Declaration of Country Name
let country = "India";
//Declaration of Unit Name
let units = "metric";
let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${WEATHER_API_KEY}&units=${units}`;
export const FetchProducts = () => {
  let weatherDetails = null;
  return async function (dispatch) {
    const fetchWeather = () => {
      axios
        .get(apiUrl)
        .then(function (response) {
          weatherDetails = response;
        })
        .catch(function (error) {});
    };
    fetchWeather();
    console.log(fetchWeather);

    if (weatherDetails) {
      dispatch(setProducts(weatherDetails));
    }
  };
};

export const setProducts = (products = null) => {
  if (products) {
    return {
      type: SET_PRODUCTS,
      payload: products,
    };
  }

  return {
    type: SET_PRODUCTS,
    payload: [],
  };
};
