import React, { useState } from "react";
import { Form, Button, Container } from "react-bootstrap";
import { Formik } from "formik";
import * as Yup from "yup";
import { Fragment } from "react";

const pinCodeRegExp = /^[1-9][0-9]*$/;

const passwordRegExp =
  /^(?=.*\d)(?=.*[A-Z])(?=.*[a-z])(?=.*[a-zA-Z!#$@^%&? "])[a-zA-Z0-9!#$@^%&?]{8,20}$/;
const validationSchema = Yup.object().shape({
  firstName: Yup.string()
    .min(2, "*First Name must have at least 2 characters")
    .max(100, "*First Name can't be longer than 100 characters")
    .required("*First Name is required"),
  lastName: Yup.string()
    .min(2, "*Last Name must have at least 2 characters")
    .max(100, "*Last Name can't be longer than 100 characters")
    .required("*Last Name is required"),
  email: Yup.string()
    .email("*Must be a valid email address")
    .max(100, "*Email must be less than 100 characters")
    .required("*Email is required"),
  city: Yup.string()
    .min(2, "*City Name must have at least 2 characters")
    .max(100, "*City Name can't be longer than 100 characters")
    .required("*City Name is required"),
  state: Yup.string()
    .min(2, "*State Name must have at least 2 characters")
    .max(100, "*State Name can't be longer than 100 characters")
    .required("*State Name is required"),
  zip: Yup.string("Must be number")
    .min(6, "*Pincode must be 6 digit")
    .max(6, "*Pincode must be 6 digit")
    .required("*Zip Code  is required")
    .matches(pinCodeRegExp, " Must be Positive Number"),
  password: Yup.string()
    .required("*Password  is required")
    .matches(
      passwordRegExp,
      "*Password min be 8 Character,Password max be 20 Character,At least one uppercase character ,At least one lowercase character ,At least one digit ,At least one special character  "
    ),
  confirmPassword: Yup.string()
    .oneOf([Yup.ref("password"), null], "Passwords must match")
    .required("Confirm Password is required"),
  terms: Yup.bool().required().oneOf([true], "*Terms must be accepted"),
  gender: Yup.string().required("Select one Value"),
  color: Yup.string().required("Color is required!"),
  multiColor: Yup.array()
    .min(1, "Please Select at least One items")
    .required("Color select is required!"),
});

const FormValidation = () => {
  const [details, setDetails] = useState({});
  return (
    <Fragment>
      <Container
        fluid
        style={{
          padding: "30px",
          paddingTop: "0px",
          paddingBottom: "15px",
        }}
      >
        <Formik
          initialValues={{
            firstName: "",
            lastName: "",
            email: "",
            city: "",
            state: "",
            zip: "",
            password: "",
            terms: false,
            gender: "",
            color: "",
            multiColor: [],
          }}
          validationSchema={validationSchema}
          onSubmit={(values, { setSubmitting, resetForm }) => {
            // When button submits form and form is in the process of submitting, submit button is disabled
            setSubmitting(true);
            // Simulate submitting to database, shows us values submitted, resets form
            setTimeout(() => {
              alert(" Data is submitted successfully");
              console.log(values);
              setDetails(values);
              resetForm();
              setSubmitting(false);
            }, 1000);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
          }) => (
            <Form onSubmit={handleSubmit} className="mt-3">
              {/*First Name input Field*/}
              {console.log(errors)}
              <Form.Group controlId="firstName" className="mb-3">
                <Form.Label> First name :</Form.Label>
                <Form.Control
                  type="text"
                  name="firstName"
                  placeholder="First Name"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.firstName}
                  isInvalid={touched.firstName && errors.firstName}
                  isValid={touched.firstName && !errors.firstName}
                />

                <Form.Control.Feedback type="invalid">
                  {errors.firstName}
                </Form.Control.Feedback>
              </Form.Group>
              {/*Last Name input Field*/}
              <Form.Group controlId="lastName" className="mb-3">
                <Form.Label> Last name :</Form.Label>
                <Form.Control
                  type="text"
                  name="lastName"
                  placeholder="Last Name"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.lastName}
                  isInvalid={touched.lastName && errors.lastName}
                  isValid={touched.lastName && !errors.lastName}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.lastName}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Password input Field*/}
              <Form.Group controlId="password" className="mb-3">
                <Form.Label> Password :</Form.Label>
                <Form.Control
                  type="text"
                  name="password"
                  placeholder="Password"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.password}
                  isInvalid={touched.password && errors.password}
                  isValid={touched.password && !errors.password}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.password}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Email input Field*/}
              <Form.Group controlId="email" className="mb-3">
                <Form.Label> Email :</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Email"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.email}
                  isInvalid={touched.email && errors.email}
                  isValid={touched.email && !errors.email}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.email}
                </Form.Control.Feedback>
              </Form.Group>

              {/*City input Field*/}
              <Form.Group controlId="city" className="mb-3">
                <Form.Label> City :</Form.Label>
                <Form.Control
                  type="text"
                  name="city"
                  placeholder="City"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.city}
                  isInvalid={touched.city && errors.city}
                  isValid={touched.city && !errors.city}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.city}
                </Form.Control.Feedback>
              </Form.Group>

              {/*State input Field*/}
              <Form.Group controlId="state" className="mb-3">
                <Form.Label> State :</Form.Label>
                <Form.Control
                  type="text"
                  name="state"
                  placeholder="State"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.state}
                  isInvalid={touched.state && errors.state}
                  isValid={touched.state && !errors.state}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.state}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Zip input Field*/}
              <Form.Group controlId="zip" className="mb-3">
                <Form.Label> Zip :</Form.Label>
                <Form.Control
                  type="text"
                  name="zip"
                  placeholder="Zip Code"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.zip}
                  isInvalid={touched.zip && errors.zip}
                  isValid={touched.zip && !errors.zip}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.zip}
                </Form.Control.Feedback>
              </Form.Group>

              {/* Radio Button Field*/}
              <Form.Group className="mb-3">
                <Form.Label as="legend" column sm={2}>
                  Gender:
                </Form.Label>

                <Form.Check
                  type="radio"
                  label="Male"
                  name="gender"
                  id="male"
                  value="male"
                  onChange={handleChange}
                  checked={values.gender === "male"}
                  isInvalid={touched.gender && !!errors.gender}
                  feedbackType="invalid"
                />
                <Form.Check
                  type="radio"
                  label="Female"
                  name="gender"
                  id="Female"
                  value="Female"
                  onChange={handleChange}
                  checked={values.gender === "Female"}
                  isInvalid={touched.gender && !!errors.gender}
                  feedback={errors.gender}
                  feedbackType="invalid"
                />
              </Form.Group>
              {/* Checkbox Field for terms*/}
              <Form.Group className="mb-3" controlId="terms">
                <Form.Check
                  name="terms"
                  label="Agree to terms and conditions"
                  onChange={handleChange}
                  isInvalid={touched.terms && !!errors.terms}
                  feedback={errors.terms}
                  feedbackType="invalid"
                  checked={values.terms}
                  id="terms"
                />
              </Form.Group>

              {/* Drop Down listField for */}
              <Form.Group className="mb-3">
                <Form.Label as="legend" column sm={2}>
                  Please Select:
                </Form.Label>
                <Form.Select
                  aria-label="Floating label select example"
                  name="color"
                  value={values.color}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  isInvalid={touched.color && errors.color}
                  isValid={touched.color && !errors.color}
                >
                  <option>Open this select menu</option>
                  <option value="1">One</option>
                  <option value="2">Two</option>
                  <option value="3">Three</option>
                </Form.Select>
                <Form.Control.Feedback type="invalid">
                  {errors.color}
                </Form.Control.Feedback>
              </Form.Group>

              {/* Drop Down listField for */}
              <Form.Group className="mb-3">
                <Form.Label as="legend" column sm={2}>
                  Please Select:
                </Form.Label>
                <Form.Select
                  aria-label="Floating label select example"
                  name="multiColor"
                  value={values.multiColor}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  isInvalid={touched.multiColor && errors.multiColor}
                  isValid={touched.multiColor && !errors.multiColor}
                  multiple={true}
                >
                  <option>Open this select menu</option>
                  <option value="1">One</option>
                  <option value="2">Two</option>
                  <option value="3">Three</option>
                </Form.Select>
                <Form.Control.Feedback type="invalid">
                  {errors.multiColor}
                </Form.Control.Feedback>
              </Form.Group>

              {/*Submit button that is disabled after button is clicked/form is in the process of submitting*/}
              <Button
                variant="primary"
                type="submit"
                className="mt-5"
                disabled={isSubmitting}
              >
                Submit
              </Button>
            </Form>
          )}
        </Formik>

        <div>
          {Object.entries(details).map(([key, value], i) => {
            return <div key={i}> {value}</div>;
          })}
        </div>
      </Container>
    </Fragment>
  );
};

export default FormValidation;
