  //       // handle error
  //       console.log(error);
  //       setLoader(false);
  //     });
  // };

  return (
    <>
      {loader ? (
        <>
          <div
            style={{
              padding: "200px",
              color: "red",
              textAlign: "center",
            }}
          >
            <CircularProgress color="secondary" />
