          </div>
          <div style={{ visibility: "hidden" }}>
            {setTimeout(() => {
              setLoader(false);
            }, 1000)}
          </div>
        </>
      ) : (
        <CommonReactTable data={postsApiData} columns={SubRowsColumns} />
      )}
