const initialState = { time: { h: 0, m: 0, s: 0 }, seconds: 0 };

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "INCREMENT":
      return {
        ...state,
        seconds: state.seconds + 60,
        time: action.secToTime(state.seconds + 60),
      };

    default:
      return state;
  }
};

export default reducer;
