import { useEffect, useState, useMemo } from "react";
import axios from "axios";
import CommonTable from "../ReusableTable/CommonTable";
import { COLUMNS } from "./Columns";
import CircularProgress from "@material-ui/core/CircularProgress";
import CommonReactTable from "../common/CommonReactTable";
const Posts = () => {
    //Columns For Table
    const columns = useMemo(() => COLUMNS, []);
    //State For Storing JsonPlaceholder Api  data
    const [users, setUsers] = useState([]);
    // State For the Loader Part
    const [loader, setLoader] = useState(true);
    //State for the ErrorMsg When Something Wrong In Fetching Api Data
    const [errorMsg, setErrorMsg] = useState(null);
    //Declaration of APi URL
    let apiUrl = `https://jsonplaceholder.typicode.com/posts`;
    //Function For Getting Api Data And Setting in users State

    // Used for All Clean up When Component is Unmounted And mounted
    useEffect(() => {
        let mounted = true;
        const fetchUser = () => {
            axios
                .get(apiUrl)
                .then(function (response) {
                    if (mounted) {
                        setUsers(response.data);
                        console.log(response);
                        setLoader(false);
                    }
                })
                .catch(function (error) {
                    // handle error
                    console.log(error);
                    setErrorMsg("Data Not Available");

                    setLoader(false);
                });
        };
        fetchUser();
        return function cleanup() {
            mounted = false;
        };
    }, []);
    // useEffect(() => {
    //     const source = axios.CancelToken.source();

    //     const fetchUsers = async () => {
    //         try {
    //             await axios.get("/users", {
    //                 cancelToken: source.token,
    //             });
    //             // ...
    //         } catch (error) {
    //             if (Axios.isCancel(error)) {
    //             } else {
    //                 throw error;
    //             }
    //         }
    //     };

    //     fetchData();

    //     return () => {
    //         source.cancel();
    //     };
    // }, []);
    const col = [
        { title: "Id", dataIndex: "id", width: 150 },
        { title: "Body", dataIndex: "body", width: 850 },
        { title: "Name", dataIndex: "name", width: 850 },
        { title: "Email", dataIndex: "email", width: 850 },
    ];

    console.log(users);
    const [comments, setComments] = useState([]);
    const renderRowSubComponent = (row) => {
        axios
            .get(
                `https://jsonplaceholder.typicode.com/posts/${row.original.id}/comments`
            )
            .then(function (response) {
                setComments(response.data);
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            });
        return (
            <CommonTable
                data={comments}
                columns={col}
                styelName={"ellipsis_class"}
                toolTip={true}
            />
        );
    };

    return (
        <div className="app">
            <div className="text-center"> {errorMsg}</div>
            {loader ? (
                <>
                    <div
                        style={{
                            padding: "200px",
                            color: "red",
                            textAlign: "center",
                        }}
                    >
                        <CircularProgress color="secondary" />
                        <h1>Loading Data Please wait...</h1>
                    </div>
                </>
            ) : (
                <>
                    {users && (
                        <CommonReactTable
                            data={users}
                            columns={columns}
                            tableTitle={"Post Management Table"}
                            showPagination={false}
                            renderRowSubComponent={renderRowSubComponent}
                        />
                    )}
                </>
            )}
        </div>
    );
};

export default Posts;
